@import url(https://fonts.googleapis.com/css?family=Roboto:400,500);
.navbar label {
  display: none;
  font-size: 30px;
  position: fixed;
  padding: 10px;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 150;
}

.nav-menu {
  background: url(24e62d29ef619d2888067a66b2a30f3f.png) #002e5b;
  list-style: none;
  margin: 0;
  display: flex;
  flex-flow: row wrap;
  justify-content: space-around;
}
.nav-menu .nav-item a {
  color: whitesmoke;
  display: block;
  padding: 15px 20px;
  text-decoration: none;
  font-size: 1.2rem;
}
.nav-menu .nav-item a:hover, .nav-menu .nav-item a:focus {
  color: #fed136;
  background: rgba(188, 202, 206, 0.15);
}

.navbar > input[type=checkbox] {
  display: none;
}
.navbar > input[type=checkbox]:checked + label {
  color: #fed136;
  font-style: normal;
}
.navbar > input[type=checkbox] + label {
  color: whitesmoke;
}

@media (max-width: 767px) {
  .nav-menu {
    flex-flow: column wrap;
    justify-content: space-around;
    padding: 0;
    position: relative;
    display: none;
  }
  .nav-menu:first-child {
    z-index: 100;
  }

  .nav-item a {
    border-top: 1px solid rgba(255, 255, 255, 0.4);
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    text-align: center;
  }

  .navbar {
    display: block;
    background: #002e5b;
    height: 50px;
    flex-flow: column wrap;
  }
  .navbar label {
    display: block;
  }
  .navbar input[type=checkbox]:checked ~ .nav-menu {
    display: flex;
    z-index: 100;
  }
}
/* Layout */
.footer {
  width: 100%;
  padding: 0;
  font-size: 1.2em;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #002e5b;
  text-align: center;
  border-top: 1px solid rgba(10, 18, 20, 0.54902);
}
.footer .footer-socials {
  margin: 60px 0 20px;
}
.footer .footer-socials a {
  display: inline-block;
  margin-right: 5px;
  background-color: #002e5b;
  color: whitesmoke;
  font-size: 20px;
  line-height: 45px;
  width: 45px;
  height: 45px;
  border-radius: 50%;
}
.footer .footer-socials a:hover {
  background-color: #fed136;
  color: #002e5b;
}
.footer .footer-bottom {
  padding: 10px 0 40px;
}

.grid-container {
  margin: 1rem 0.5em;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
}

.grid-container .grid-item {
  display: flex;
  justify-content: center;
  align-items: center;
}

.grid-container article img {
  padding: 0;
  max-width: 100%;
}

.product {
  background-color: lightgrey;
  text-align: center;
  border-radius: 10px;
  width: 250px;
  height: 360px;
  position: relative;
  overflow: hidden;
  box-shadow: rgba(10, 18, 20, 0.54902) 0px 4px 13px 3px;
}

.product-back {
  width: 250px;
  height: 360px;
  border-radius: 10px;
  background-color: lightgrey;
  color: #EE3B24;
  text-align: center;
  margin: 0 auto;
  position: relative;
  overflow: hidden;
  box-shadow: rgba(10, 18, 20, 0.54902) 0px 4px 13px 3px;
  backface-visibility: hidden;
  z-index: -1;
  top: -360px;
  transform: rotateY(180deg);
}

.product-name {
  padding: 0.5rem 0;
  font-size: 1em;
  color: #002e5b;
  background-color: lightgrey;
}

.product-menu {
  width: 100%;
  display: flex;
  justify-content: space-between;
  height: 360px;
  background-color: lightgrey;
  color: #002e5b;
  position: absolute;
  top: 80%;
  transition: all 0.7s;
}

.product-price, .buy-now {
  position: relative;
  font-size: 1.1rem;
  padding-top: 20px;
  width: 50%;
  color: #002e5b;
  border-top: solid 1px rgba(10, 18, 20, 0.54902);
}

.buy-now {
  border-left: solid 1px rgba(10, 18, 20, 0.54902);
  cursor: pointer;
  transition: all 0.3s;
}

.buy-now:hover {
  background-color: #fed136;
  color: #002e5b;
}

.product-detail, .cancel, .add-to-cart, .how-many {
  display: none;
}

.product-wrapper {
  -webkit-transition: all 0.7s ease;
  display: inline-block;
  margin: 1em;
  height: 360px;
  transition: all 0.7s ease;
  -webkit-transform-style: preserve-3d;
  transform-style: preserve-3d;
}

.product .icon {
  display: block;
}

.product .icon .icon-background {
  width: 80px;
  height: 80px;
  line-height: 80px;
  border-top-left-radius: 10px;
  background-color: #002e5b;
  position: absolute;
  top: 0px;
  left: -40px;
  transform: skewX(-45deg);
}

.product .icon .shopping-cart {
  color: whitesmoke;
  font-size: 1.5em;
  display: block;
  position: absolute;
  top: 8px;
  left: 8px;
}

.product .icon .shopping-cart:hover {
  color: #fed136;
}
